import React from "react";
export default function About(props){
    return (
        <div className="container">
        <h1 style={{color: props.mode==='dark'?'white':'black'}}>This is about section</h1>
        <p style={{color: props.mode==='dark'?'white':'black'}}>Pure Text is a text utility where you can convert text in uppercase, lowercase and you can even copy the text by just simply one click.! &#128516; </p>
        </div>
    )
}
